<?phpclass Program{    public static function getProgram($program, $action)    {        $src = 'program/'.strtolower($program);        if(!file_exists($src))            Error::brut('Program '.$program.' introuvable');        include_once $src.'/'.$action;        return true;    }	public static function load($program)	{	    $program = strtolower($program);		$src = 'program/'.$program;		if(!file_exists($src))			Error::brut('Program '.$program.' introuvable');		$actions = ScanDossier::fileOnly($src, false);		foreach($actions AS $action)		{			$src = 'program/'.$program.'/'.$action;			if(!is_readable($src))				Error::brut('Impossible d\'ouvrir le sous-programme '.$src);			include_once $src;		}		return true;	}}?>