<?php/** * @Version(name="User", version="1.0") */class User{	private $_id;	private $_mdp;	private $_email;	private $_pseudo;	private $_ext;	public function __construct(array $donnees = array()){		if(!empty($donnees))			Hydrate::objKeys($this, $donnees);	}	/* getters */	public function id(){ return (int) $this->_id;}	public function mdp(){ return $this->_mdp;}	public function email(){ return $this->_email;}	public function pseudo(){ return $this->_pseudo;}	public function user(){ return $this;}	public function ext($index=false){ 		if($index === false)			return $this->_ext;		else			return $this->_ext[$index];	}	/* setters */	public function setId($id)	{		$id = (int) $id;		if ($id > 0)		  $this->_id = $id;	}   	public function setPseudo($pseudo)	{		$pseudo = rtrim(Secur::aZ09($pseudo));		$length = strlen($pseudo);		if($length <= 1 || $length >= 26)			return false;		$this->_pseudo = $pseudo;		return true;	}	public function setMdp($mdp)	{		$length = strlen($mdp);		if($length <= 4 || $length >= 26)			return false;		$mdp = sha1($mdp);		$this->_mdp = $mdp;		return true;	}	public function setEmail($email)	{		$length = strlen($email);		if(!Secur::email($email) || $length <= 1 || $length >= 250)			return false;		$this->_email = $email;		return true;	}	public function setExt(array $extension)	{		$this->_ext = $extension;	}} ?>