<?phpclass Viewer{	public static function lsL($src, $stock, $vrac)	{		$sep = '';		if(empty($vrac))			return 'Dossier vide.';		if(empty($stock))			$stock = 0;		$return = '<table class="tabExplorer center">';		$racine = '../lib-177/'.substr($src, 2);		foreach($vrac AS $key => $file)		{			if($file['type'] == 1){				if($stock === 0)					$path = $file['name'];				else					$path = $stock.','.$file['name'];				$type = '<a class="actionBT btFile left" href="ftp-see-'.$path.'-0">					<img src="library/img/folder.png" alt="folder">'.substr($file['name'], 0, 20).'				</a>				<a class="actionBT litleActBT" href="ftp-deleteDir-'.$stock.'-'.$file['name'].'" title="Supprimer"><img src="library/img/croix.png" alt="supprimer"></a>				<a href="ftp-propageDir-'.$stock.'-'.$file['name'].'" class="litleActBT"><img src="library/img/servers.png" alt="Propagé" title="Propagé '.$file['name'].' dans les autres projets."></a>';			}			else				$type = '<a class="actionBT btFile left" href="ftp-open-'.$stock.'-'.$file['name'].'" title="Enregistrer sur '.$racine.$file['name'].'">					<img src="library/img/page.png" alt="page">'.substr($file['name'], 0, 20).'				</a>				<a class="actionBT litleActBT" href="ftp-delete-'.$stock.'-'.$file['name'].'" title="Supprimer"><img src="library/img/croix.png" alt="supprimer"></a>				<a href="ftp-propage-'.$stock.'-'.$file['name'].'" class="litleActBT"><img src="library/img/servers.png" alt="Propagé" title="Propagé '.$file['name'].' dans les autres projets."></a>';			$return .= '			<tr>				<td>					<span>'.$file['access'].'</span>				</td>				<td>					<span>'.$file['groupe'].'</span>				</td>				<td>					<span>'.$file['pid'].' Kb</span>				</td>				<td>					<span>'.$file['date'].'</span>				</td>				<td>					'.$type.'				</td>			</tr>';		}		return $return.'</table>';	}	public static function ls($src, $stock, $vrac)	{		if(empty($vrac))			return 'Dossier vide.';		$return = '';		if(empty($stock))			$stock = 0;		foreach($vrac AS $key => $file)		{			if($file['type'] == 1){				if($stock === 0)					$path = $file['name'];				else					$path = $stock.','.$file['name'];				$return .= '				<div class="menuExplorer">					<a class="actionBT btFile" href="ftp-see-'.$path.'-0"><img src="library/img/folder.png" alt="folder">'.substr($file['name'], 0, 20).'</a>					<a class="litleActBT" href="ftp-delete-'.$stock.'-'.$file['name'].'" title="Supprimer"><img src="library/img/croix.png" alt="supprimer"></a>					<a href="ftp-propage-'.$stock.'-'.$file['name'].'" class="litleActBT"><img src="library/img/servers.png" alt="Propagé" title="Propagé '.$file['name'].' dans les autres projets."></a>				</div>';			}			else				$return .= '				<div class="menuExplorer">					<a class="actionBT btFile" href="ftp-open-'.$stock.'-'.$file['name'].'"><img src="library/img/page.png" alt="page">'.substr($file['name'], 0, 20).'</a>					<a class="litleActBT" href="ftp-delete-'.$stock.'-'.$file['name'].'" title="Supprimer"><img src="library/img/croix.png" alt="supprimer"></a>					<a href="ftp-propage-'.$stock.'-'.$file['name'].'" class="litleActBT"><img src="library/img/servers.png" alt="Propagé" title="Propagé '.$file['name'].' dans les autres projets."></a>				</div>';		}		return $return;	}	public static function mvc($src, $stock, $vrac)	{		if(empty($vrac))			return;		$return = '';		foreach($vrac as $file => $keys)		{			$return .= '				<div class="explorerMVC">    					<a href="ftp-openMVC-'.$stock.'-all-'.$file.'">'.$file.'</a>					<a href="ftp-openMVC-'.$stock.'-ctr-'.$file.'">ctr</a>					<a href="ftp-openMVC-'.$stock.'-mod-'.$file.'">mod</a>					<a href="ftp-openMVC-'.$stock.'-vue-'.$file.'">vue</a>					<a href="ftp-deleteMVC-'.$stock.'-'.$file.'" class="litleActBT" title="Supprimer"><img src="library/img/croix.png" alt="supprimer"></a>					<a href="ftp-propage-'.$stock.'-'.$file.'" class="litleActBT"><img src="library/img/servers.png" alt="Propagé" title="Propagé '.$file.' dans les autres projets."></a>				</div>';               		}		return $return;	}	public static function ftpFavor($favoris)	{		if(empty($favoris))			return 'Aucun favori.';		$return = '';		foreach($favoris as $favor)		{				$return .= '				<div class="menuExplorer">					<a class="actionBT btFile" href="ftpFavor-connect-'.$favor.'"><img src="library/img/link.png" alt="link">'.substr($favor, 0, 20).'</a>					<a class="litleActBT" href="ftpFavor-delete-'.$favor.'" title="Supprimer"><img src="library/img/croix.png" alt="supprimer"></a>					<a href="ftpFavor-modif-'.$favor.'" class="litleActBT"><img src="library/img/modif.png" alt="Modifier"></a>				</div>';             		}		return $return;	}}?>