<?phpclass VirtualFlux{	public static function drip(){		if(!empty($_POST)){			VirtualFlux::saveDrip();/* Stock $_GET $_POST et $_FILE dans $_SESSION et redirige */		}		elseif(isset($_SESSION['savePost'])){			VirtualFlux::getDrip();/* Stock $_SESSION[GET, POST, FILE] dans $_GET $_POST et $_FILE virtuel*/			return true;		}		else{			return false;/* Aucune donnée recue */		}	}	private static function saveDrip(){		$_SESSION['savePost'] = $_POST;		header('Refresh:0;');		die;	}	private static function getDrip(){		$_POST = $_SESSION['savePost'];		unset($_SESSION['savePost']);	}}?>