<?php$name = 'GreyList';$titre = 'Class '.$name;$localisation = 'library/php/autoload/';$introduction = 'Cette fonction soumet une variable à une white list ou une black list fournie dans une tableau.';$structure = array(	array('name' => $name.'::whiteList', 'return' => 'bool', 		'comment' => 'renvoie true si $var est une des valeur de $black_list',		'param' => array(		array('name' => 'white_list', 'type' => 'array'),		array('name' => 'var', 'type' => 'mixed')		)	),	array('name' => $name.'::blackList', 'return' => 'bool', 		'comment' => 'renvoie false si $var est une des valeur de $black_list',		'param' => array(		array('name' => 'black_list', 'type' => 'array'),		array('name' => 'var', 'type' => 'mixed')		)	));$exemple = '$choix_accepte = array(\'oui\', \'non\', \'peut être\');if('.$name.'::whiteList($choix_accepte, $_GET[\'user_reponse\']))	<span class="indent"></span>echo \'Choix valide \';';?>