<?php$name = 'Hydrate';$titre = 'Class '.$name;$localisation = 'library/php/autoload/';$introduction = 'Cette class hydrate vos objets automatiquement avec des données fournies par tableau,				via les setters de la class qui le demande, si elle les trouvent.<br>				Pour que les setters soit trouvés veuillez suivre la convention de nomage:<br>				$variable, setVariable()';$structure = array(	array('name' => $name.'::objKeys', 'return' => 'void',		'param' => array(			array('name' => 'this', 'type' => 'obj'),			array('name' => 'keys', 'type' => 'array')		)	));$exemple = 'public function __construct(array $keys){	if(!empty($keys)){		<span class="indent"></span>'.$name.'::objKeys($this, $keys);	}}';?>