<?php$src = 'error/';function getMsg($dest){	if(!file_exists($dest))		return 'Introuvable';	return file_get_contents($dest);}if(isset($_POST['id']) && !empty($_POST['id'])){	$id = (int) $_POST['id'];	$stock[0] = array(		'code' => $id,		'msg' => getMsg($src.$id.'.php')	);	return;}$stock = ScanDossier::fileOnly($src, false);usort($stock, "strnatcmp");foreach($stock as $index => $name){	$stock[$index] = array(		'code' => substr($name, 0, -4),		'msg' => getMsg($src.$name)	);}