<?php$dialNewPage = '';if($etat){	if(empty($_POST['numPage']) OR empty($_POST['msgError']))		$dialNewPage = 'Veuillez remplire tout les champs';	else{		$erreur = NewPage::error($_POST['numPage'], $_POST['msgError']);		$dialNewPage = 'Création de la page <a href="error-'.$_POST['numPage'].'">Error n°'.$_POST['numPage'].'</a><br><br>';		if(empty($erreur))			$dialNewPage .= 'SUCCES';		else			$dialNewPage .= 'Erreurs: '.$erreur;	}}else	$dialNewPage = 'Enregistre le message dans error/messages/[numError].php.<br><br>	Serat appelé par Error::code([numError]);<br>	<br>Accesible à l\'url: <a href="error-404">error-[numError]</a>';?>