<?phpnamespace Explore;function delete($src, $project, $stock, $cible, $action = 'file'){	if(!file_exists($src))		\Error::brut('File unknown.');	switch($action)	{		case 'file':			\Program::getProgram('core177', 'delete.php');			return \Core177\delete($src, $cible);		break;		case 'mvc':			\Program::getProgram('core177', 'delete.php');			\Core177\delete($src.'controleur/', 'ctr.'.$cible.'.php');			\Core177\delete($src.'model/', 'mod.'.$cible.'.php');			\Core177\delete($src.'vue/', 'vue.'.$cible.'.php');		break;		case 'dir':			\Program::getProgram('core177', 'deleteDir.php');			\Core177\deleteDir($src.$cible);		break;		default:			\Error::brut('Action unknown.');	}}?>