<?phpnamespace Explore;function listDirectory($src, $project, $stock = 0){	if(!file_exists($src))		return false;  $directorys = \ScanDossier::dirOnly($src, false);  if(empty($directorys))    return false;      $return = '';	foreach($directorys as $directory)  {		$date = date('d/m/y h:i:s', filemtime($src.$directory));		$size = stat($src.$directory);		$stockSee = $directory;		if(!empty($stock))			$stockSee = $stock.','.$stockSee;    if(empty($project) && $src == '../'){			$projectCible = $directory;			$stock = $stockSee = $directoryLink = 0;		}		else{      $projectCible = $project;			$directoryLink = $directory;    }		$return .= '		<nav class="bTmodeCase">			<a class="btFold" href="explore-see-'.$projectCible.'-'.$stockSee.'-0"></a>			<a href="explore-propageDir-'.$projectCible.'-'.$stock.'-'.$directoryLink.'" class="propage">				<img src="library/img/servers.png" alt="Propager" title="Propager dans les autres projets.">			</a>			<a href="explore-deleteDir-'.$projectCible.'-'.$stock.'-'.$directoryLink.'" class="delete">				<img src="library/img/croix.png" alt="supprimer" title="Supprimer">			</a>			<span>'.$size['size'].'Kb</span>			<span>'.substr($date, 0, -9).'</span>			<span>'.substr($date, 9).'</span>			<input name="rename" value="'.$directory.'">		</nav>';	}  return $return;}?>