<?phpnamespace Explore;function slide($exploProject = false, $destination = 0){    $directorys = \ScanDossier::dirOnly('../', false);	$slider = '';    if(empty($directorys))        return false;	foreach($directorys as $directory){		if($directory === $exploProject)			$class = ' class="slideBTactive"';		else			$class = '';		$slider .= '<a'.$class.' href="explore-see-'.$directory.'-'.$destination.'-0">'.$directory.'</a>';	}	return $slider;}?>