<?phpnamespace Explore;function typedPage($namePage, $type){    switch($type){        case 'ctr':            $namePage = 'controleur/ctr.'.$namePage.'.php';        break;        case 'mod':            $namePage = 'model/mod.'.$namePage.'.php';        break;        case 'vue':            $namePage = 'vue/vue.'.$namePage.'.php';        break;        case 'js':            $namePage = $namePage.'.js';        break;        case 'cfg':            $namePage = 'cfg.'.$namePage.'.php';        break;        case 'css':            $namePage .= '.css';        break;        case 'htc':            $namePage = '.'.$namePage;        break;        case 'nfo':            $namePage = 'info.'.$namePage.'.php';        break;        case 'atl':            $namePage = 'class.'.$namePage.'.php';        break;        case 'atm':            $namePage = 'atm.'.$namePage.'.php';        break;        default:            Error::brut('Type unknown.');    }    return $namePage;} ?>