<?phpfunction QueryFrag($fragAction, $project, $stock, $name){    $src = '../';    if(!empty($project))        $src .= $project.'/fragments/';    if(!empty($stock))        $src .= $stock.'/';    if(!file_exists($src)){        echo 'admin/program/exploreQuery dit: "'.$src.'" inaccessible.';        die;    }    switch($fragAction)    {        case 'alterFrag':            Program::getProgram('fragments', 'alterFrag.php');            return alterFrag($src, $project, $stock, $name);        break;        case 'compacte':            Program::getProgram('fragments', 'compacte.php');            return compacte($project, $stock);        break;    }}?>