<?phpfunction listProject(){    $src = '../';    if(!file_exists($src))        die('program/Explore/listProject dit: Dossier des projets inaccessible.');    $projects = ScanDossier::dirOnly($src, false);    if(empty($projects))        return false;    foreach($projects as $project){        $return[$project] = 'fragments-listStock-'.$project.'-none-none';    }    return $return;}?>