<?phpfunction listStock($project){    $src = '../'.$project.'/fragments/';    if(!file_exists($src)){        echo 'programADM listStock dit: Stock de fragments inaccessible.';        die;    }    $stocks = ScanDossier::dirOnly($src, false);    if(empty($stocks))        return false;    foreach($stocks as $stock){        $return[$stock] = 'fragments-seeFrags-'.$project.'-'.$stock.'-none';    }    return $return;}?>