<?phpfunction listDirectory($src, $project, $stock = false){    $directorys = ScanDossier::dirOnly($src, false);    if(empty($directorys))        return false;	if(empty($project))		$addr = 'explore-see-';	elseif(empty($stock))		$addr = 'explore-see-'.$project.'-';    else		$addr = 'explore-see-'.$project.'-'.$stock.',';    $return = '';	foreach($directorys as $directory){		$return .= '<div class="menuExplorer">			<a class="whiteBT btFold" href="'.$addr.$directory.'-0"><img src="library/img/folder.png" alt="dossier">'.$directory.'</a>		</div>';	}    return $return;}?>