<?phpfunction propage($projectCurent, $stock, $name){	if(empty($projectCurent)){		echo 'Program/explore/propage dit: projet nécessaire afin de propager.';		return false;	}	$src = '../'.$projectCurent.'/';    if(!empty($stock))            $src .= str_replace(',', '/', $stock).'/';    Program::getProgram('core177', 'newDir.php');	$original = $src.$name;	$projects = ScanDossier::dirOnly('../', false);	foreach($projects AS $projectCible)	{		if($projectCible == $projectCurent)			continue;		$mapping = '../'.$projectCible;		if(!empty($stock))				$map = explode(',', $stock);		while(!empty($map)){			$last = array_shift($map);			if(!file_exists($mapping.'/'.$last)){				newDirectory($mapping.'/', $last);			}			$mapping .= '/'.$last;		}		if(!copy($original, $mapping.'/'.$name))			return false;	}	return true;} ?>