<?phpfunction see($ftp, $src, $stock = '', $cible){	if(!empty($cible)){		Program::getProgram('ftp', 'open.php');		openFile($ftp, $src, $stock, $cible);	}	Program::getProgram('ftp', 'editBar.php');	$head = editBar($src, $stock);	if(isset($_SESSION['mode']))		$fichiers = Viewer::ls($src, $stock, $ftp->ls($src));	else		$fichiers = Viewer::lsL($src, $stock, $ftp->ls($src));	$verif = explode(',', $stock);    if(isset($verif[1]) && 'mvc' == $verif[1] && !isset($verif[2]))		$fichiers .= Viewer::mvc($src, $stock, $ftp->mvc($src));    return $head.$fichiers;}?>