<?phpfunction slide($stock){	$mode1 = $mode2 = 'slideBTactive';	if(isset($_SESSION['mode']))		$mode1 = 'slideBTveille';	else		$mode2 = 'slideBTveille';	$slider = $src = '';	if(isset($_SESSION['server']))	{		$src = '<a href="ftp">'.$_SESSION['server'].'</a>/';		if(!empty($stock)){			if(!empty($stock)){				$stocks = explode(',', $stock);				foreach($stocks as $oneStock){					$buffer[] = $oneStock;					$src .= '<a href="ftp-see-'.implode(',', $buffer).'-0">'.$oneStock.'</a>/';				}			}		}		$src = '<h4 class="menuExplorer">Vous êtes ici: '.$src.'</h4>';		$slider = '<form action="ftp" method="post" title="Déconnexion"><input type="submit" name="logout" value="Connecter à '.$_SESSION['server'].'" class="slideBTactive pointe"></form><form action="" method="post"><input name="mode" value="on" type="image" src="library/img/list.png" class="'.$mode2.'" title="Vue détaille"></form><form action="" method="post"><input name="mode" value="off" type="image" src="library/img/simple.png" class="'.$mode1.'" title="Vue simple"></form>';	}	else{		$server = $pseudo = '';		if(isset($_POST['server']))			$server = htmlspecialchars($_POST['server']);		if(isset($_POST['pseudo']))			$pseudo = htmlspecialchars($_POST['pseudo']);		$slider = '		<form action="ftp" method="post">			<input name="server" value="'.$server.'" placeholder="server"><input name="pseudo" value="'.$pseudo.'" placeholder="pseudo"><input name="mdp" placeholder="mdp" type="password"><input type="submit" value="Connection">		</form>';	}    return '	<nav class="slide">	'.$slider.'	</nav>	'.$src;}?>