<?phpfunction slideFavor(){	$server = $pseudo = $name = '';	if(isset($_POST['server']))		$server = htmlspecialchars($_POST['server']);	if(isset($_POST['pseudo']))		$pseudo = htmlspecialchars($_POST['pseudo']);	if(isset($_POST['name']))		$name = htmlspecialchars($_POST['name']);    return '	<nav class="slide">	<form action="ftpFavor" method="post">		<input name="name" value="'.$name.'" placeholder="name"><input name="server" value="'.$server.'" placeholder="server"><input name="pseudo" value="'.$pseudo.'" placeholder="pseudo"><input name="mdp" placeholder="mdp" type="password"><input name="port" placeholder="port"><input type="submit" value="Ajouter">	</form>	</nav>';}?>