<?phpfunction QueryProject($project, $action){    switch($action)    {        case 'delete':            Program::getProgram('projects', 'delete.php');            return delete($project);        break;        case 'list':            Program::getProgram('projects', 'listProject.php');            return listProject();        break;        case 'new':            Program::getProgram('projects', 'newProject.php');            return newProject($project);        break;        case 'switcher':            Program::getProgram('projects', 'switcher.php');            return switcher($project);        break;        default:            echo 'admin/program/QueryProject dit: action inconnue.';            die;    }}?>