<?php/** * @Version(name="Foot", version="1.0") */class Foot{	public static function insert(array $script_perso = array())	{		$scripts = '';		$GlobalScript = Config::package('foot', 'GlobalScript');		if(!empty($GlobalScript)){			foreach($GlobalScript as $script){				$scripts .= "\n".'<script src="library/js/'.$script.'"></script>';}		}		if(isset($script_perso))		{			foreach($script_perso as $script){				$scripts .= "\n".'<script src="library/js/'.$script.'"></script>';}		}// Affiche un encare prenant tout l'écran prévenant que js est désactivé et proposant d'ignorer le message \\		if(!isset($_SESSION['javascript_off']))		{			$scripts .= '<noscript>	<h2>Javascript est désactivé!</h2>	<a href="http://fr.wikipedia.org/wiki/JavaScript" target="_blank">	Javascript</a> est désactivé dans votre navigateur, ce qui a pour conséquence	de désactiver certaines fonctionalitées de ce site, utiles à votre confort	et au bon déroulement de l\'expérience visuel que nous vous proposons.<br>	Nous vous conseillons de le réactiver si vous souhaitez utiliser agréablement ce site.	<h5><a href="javascriptoff">Ignorer cette avertissement et continuer la visite du site sans javascript</a></h5></noscript>';		}		include Config::srcData().'/foot.php';	}}