<?php/** * @Version(name="Hydrate", version="1.0") */class Hydrate{	public static function DripDry(array $eau, $objetSec)	{		if(get_magic_quotes_gpc())		{			$_POST = array_map('stripslashes', $_POST);			$_GET = array_map('stripslashes', $_GET);			$_COOKIE = array_map('stripslashes', $_COOKIE);			echo 'MagicQuotes étouffé dans l\'oeuf.<br>La directive magic_quotes_gpc de php.ini est activé!';		}		foreach($eau as $nom => $goutte)		{			$method = 'set'.ucfirst($nom);			if(method_exists($objetSec, $method))			{				$objetSec->$method($goutte);			}			else				Error::code(4);		}	}} ?>