<?phpclass Navigator{	private $position;	private $tableau;		public function __construct(array $values){		$this->tableau = $values;		$this->position = 0;	}	public function value()	{		return $this->tableau[$this->position];	}	public function key()	{		return $this->position;	}	public function next()	{		$this->position++;	}	public function previous()	{		$this->position--;	}	public function raz()	{		$this->position = 0;	}	public function valid()	{		return isset($this->tableau[$this->position]);	}}?>