<?php/** * @Version(name="User", version="1.0") */class User{	private $_id;	private $_pseudo;	private $_mdp;	private $_ext;	public function __construct(array $donnees){		if(!empty($donnees)){			Hydrate::objKeys($this, $donnees);		}			}	/* getters */	public function id(){ return $this->_id;}	public function pseudo(){ return $this->_pseudo;}	public function mdp(){ return $this->_mdp;}	public function user(){ return $this;}	public function ext($index=false){ 		if($index===false)			return $this->_ext		else			return $this->_ext[$index];	}	/* setters */	public function setId($id)	{		$id = (int) $id;		if ($id > 0)		  $this->_id = $id;	}   	public function setPseudo($pseudo)	{		if(Secur::aZNb($pseudo, 2, 25))			$this->_pseudo = $pseudo;	}	public function setMdp($mdp)	{		$length_mdp = strlen($mdp);		if($length_mdp >= 2 && $length_mdp <= 24)			$this->_mdp = $mdp;	}	public function setExt(array $extension)	{		$this->_ext = $extension;	}} ?>